import React from 'react';

export default function Hero() {
  const heroItems = [
      {
        title: 'Contact me',
        subtitle: 'Let\'s get connected',
        link: '/contact'
      },
      {
        title: 'About me',
        subtitle: 'Bio & Resumé',
        link: '/about'
      },
      {
        title: 'More projects...',
        subtitle: 'See what else I\'ve built',
        link: '/projects',
      }
    ]

  return (
    <div className="hero">
      {heroItems.map(item => (
        <a className={"hero__card hero__card-" + heroItems.indexOf(item)} href={item.link}>
          <h2 className="hero__title">{item.title}</h2>
          <h3 className="hero__subtitle">{item.subtitle}</h3>
        </a>
      ))}
    </div>
  );
}
