import React, { useState } from 'react';

import Card from './Card';

import CratesLogo from '../assets/images/Crates_Media_Logo.png';
import AthamesLogo from '../assets/images/Athames_Banner.png';
import Placeholder from '../assets/images/yikes.png';

export default function Hero() {
  const [state, setState] = useState({
    items: [
      {
        id: 0,
        title: 'Crates Media',
        subTitle: 'Independent Digital Services Agency',
        imgSrc: CratesLogo,
        link: 'http://crates.media',
        selected: false,
      },
      {
        id: 1,
        title: 'About Me',
        subTitle: 'A bit more about who I am',
        imgSrc: AthamesLogo,
        link: '/about',
        selected: false,
      },
      {
        id: 2,
        title: 'More Projects...',
        subTitle: "See what else I've built",
        imgSrc: Placeholder,
        link: 'https://github.com/ekg-91',
        selected: false,
      },
    ],
  });

  const handleCardClick = (id, card) => {
    const items = [...state.items];

    items[id].selected = !items[id].selected;

    items.forEach((item) => {
      if (item.id !== id) {
        item.selected = false;
      }
    });

    setState({
      items,
    });
  };

  const makeItems = (items) => {
    return items.map((item) => {
      return (
        <Card
          item={item}
          click={(e) => handleCardClick(item.id, e)}
          key={item.id}
        />
      );
    });
  };

  return <div className="hero">Hero</div>;
}
